    <>
      <ul className="categories">
        <li>
          <h3>Choose your category</h3>
        </li>
        {loadingCategories ? (
          <LoadingBox></LoadingBox>
        ) : errorCategories ? (
          <MessageBox variant="danger">{errorCategories}</MessageBox>
        ) : (
          categories.map((c) => (
            <li key={c}>
              <Link to={`/search/category/${c}`}>{c}</Link>
            </li>
          ))
        )}
      </ul>
    </>

